#
# Structure for table "article"
#

CREATE TABLE `article` (
  `article_id` int(11) NOT NULL AUTO_INCREMENT,
  `article_title` varchar(100) NOT NULL COMMENT '文章标题',
  `article_instro` varchar(200) NOT NULL COMMENT '文章简介',
  `article_uid` int(11) NOT NULL COMMENT '用户ID',
  `article_ctime` date NOT NULL COMMENT '文章创建时间',
  `article_thumb` varchar(200) NOT NULL COMMENT '文章缩略图',
  `article_views` int(11) NOT NULL COMMENT '文章浏览数',
  `article_content` text NOT NULL COMMENT '文章内容',
  PRIMARY KEY (`article_id`)
) ENGINE=MyISAM AUTO_INCREMENT=35 DEFAULT CHARSET=utf8 COMMENT='文章表';

