# Data for table "user"
#

INSERT INTO `user` VALUES (1,'ipso','123456@qq.com','123645654865','25d55ad283aa400af464c76d713c07ad'),(2,'ipsozz','1234226@qq.com','1245545654865','25d55ad283aa400af464c76d713c07ad'),(3,'ipsoz','123456222@qq.com','123645554865','25d55ad283aa400af464c76d713c07ad'),(4,'ipsozzz','12345611@qq.com','123665654865','25d55ad283aa400af464c76d713c07ad'),(5,'gqm','12345556@qq.com','123645888865','25d55ad283aa400af464c76d713c07ad'),(6,'test','123333456@qq.com','12364599994865','25d55ad283aa400af464c76d713c07ad');
